real a=sqrt(10),b=sqrt(26);
axes(-7,7,-7,7);
e(a,b,0,0);
xh(a,b,0,0,-6,6);
dot((0,4)^^(0,-4)); dot((6,0)^^(-6,0));
[/asy]

