draw((-5,0)--(100,0),EndArrow);
draw((0,-23)--(0,23),EndArrow);
draw(P--Q--S--R--P);
dot("$(48,0)$",P,SW);
dot("$(60,15)$",Q,N);
dot("$(60,-15)$",R,-N);
dot("$(80,0)$",S,2*SSE);
label("$x$",(100,0),N);
label("$y$",(0,23),E);
[/asy]

