path[] tofill = Circle((1.3,8.5),.15) ^^ Circle((2.1,8.5),.15) ^^ Circle((2.9,8.5),.15) ^^ Circle((8.5,1.3),.15) ^^ Circle((8.5,2.1),.15) ^^ Circle((8.5,2.9),.15);

