draw((16,-20)--(-3,-20)--(0,0)--(-3,20)--(16,20));
draw((0,0)--(16,0));
draw((5,20)--(8,0)--(5,-20));
draw((13,20)--(16,0)--(13,-20));
dot((18,0));
dot((20,0));
dot((22,0));
draw((24,0)--(50,0));
draw((23,20)--(47,20)--(50,0)--(47,-20)--(21,-20));
draw((23,20)--(26,0)--(23,-20));
