draw(a--p);
draw(c--r);

v=(1/3)*B+(2/3)*E;
x=(2/3)*B+(1/3)*E;
b=(1/3)*A+(2/3)*D;
h=(2/3)*A+(1/3)*D;

draw(v--b);
draw(x--h);

s=(1/3)*C+(2/3)*A;
u=(2/3)*C+(1/3)*A;
d=(1/3)*F+(2/3)*D;
f=(2/3)*F+(1/3)*D;

draw(s--d);
draw(f--u);

g=(1/3)*C+(2/3)*F;
